pragma solidity 0.4.25;

contract Chat {

    uint MESSAGES_PER_PACKAGE = 20;
    uint public packageIndex = 0;
    uint accumulatedMessages = 0;
    mapping(uint => uint) blockNumberLookup;
    mapping(address => string) public userName;
    mapping(bytes32 => address) public nameLookup;

    event messages(address sender, string messages, uint256 time);
    event updateName(address sender);

    constructor() public {
        blockNumberLookup[packageIndex] = block.number;
    }

    function setName(string name) public {
        bytes32 hashedName = getHashedString(name);
        address isNameTaken = nameLookup[hashedName];
        require(isNameTaken == 0x0, "Name is already taken");
        string memory prevName = userName[msg.sender];
        delete nameLookup[getHashedString(prevName)];
        nameLookup[hashedName] = msg.sender;
        userName[msg.sender] = name;
        emit updateName(msg.sender);
    }
    function getName(address addr) public view returns(string) {
        return userName[addr];
    }
    function sendMessage(string message) public {
        accumulatedMessages += 1;
        emit messages(msg.sender, message, now);
        if (accumulatedMessages >= MESSAGES_PER_PACKAGE) {
            packMessages();
        }
    }
    function getHashedString(string str) private pure returns(bytes32) {
        bytes memory strBytes = bytes(str);
        return keccak256(strBytes);
    }
    function packMessages() private {
        packageIndex += 1;
        blockNumberLookup[packageIndex] = block.number;
        accumulatedMessages = 0;
    }
    function getBlockNumber(uint index) public view returns(uint) {
        return blockNumberLookup[index];
    }
}
